INSERT INTO `craft_matrixcontent_body` (`id`, `elementId`, `locale`, `field_text_text`, `field_table_table`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('1', '7', 'en_us', '<h2>What You Need to Know</h2>\r\n\r\n<ul><li>You can pay online using Visa, MasterCard, Discover or American Express</li><li>You have 21 days after the date the ticket was written to make a payment.</li></ul>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<h2>Ways to Pay</h2>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<h3>In Person</h3>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>You can pay your ticket at the Parking Citation Assistance Center</p>', NULL, '2015-12-04 23:22:29', '2015-12-04 23:48:13', '78fbaeab-070d-4365-b3ca-fd022191afbe');
