INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_CTATitle`, `field_leadIn`, `field_CTALink`, `field_body`, `field_postDate`, `field_summary`, `field_about`, `field_departmentHead`, `field_departmentstaff`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('6', '6', 'en_us', 'Pay a Parking Ticket', 'Pay your ticket online', 'This page provides information on how to pay a parking ticket in the City of Oakland', 'https://prodpci.etimspayments.com/pbw/include/oakland/input.jsp', '<h2 id=\"what-you-need-to-know\">What You Need to Know</h2>\r\n\r\n<ul>\r\n  <li>You can pay online using Visa, MasterCard, Discover or American Express</li>\r\n  <li>You have 21 days after the date the ticket was written to make a payment.</li>\r\n</ul>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<h2 id=\"ways-to-pay\">Ways to Pay</h2>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<h3 id=\"in-person\">In Person</h3>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>You can pay your ticket at the Parking Citation Assistance Center.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<table>\r\n<thead>\r\n<tr><th>Location</th><th>Hours</th></tr>\r\n</thead>\r\n<tbody>\r\n<tr><td style=\"background-color:white\">\r\nParking Citation Assistance Center\r\n250 Frank H. Ogawa Plaza, Suite 6300\r\nOakland, CA 94612\r\nPhone: (800) 500-6484\r\n</td><td>\r\nMondays, Tuesdays, Thursdays and Fridays from 9 a.m. to 4:30 p.m.\r\nWednesdays from 9:30 a.m. to 4:30 p.m.\r\n</td></tr>\r\n</tbody>\r\n</table>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><strong>Additional locations:</strong></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<ul>\r\n  <li>Parking ticket payment kiosks at 250 Frank H. Ogawa Plaza, Monday through Friday from 8:30 a.m. to 5 p.m.</li>\r\n  <li>Eastmont Police Substation, 2651 73rd Avenue, Monday through Saturday from 8:00 a.m. to 4 p.m.</li>\r\n</ul>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<h3 id=\"by-mail\">By Mail</h3>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Mail your parking ticket and the full payment to:</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Parking Citation Assistance Center</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>250 Frank H. Ogawa Plaza, Suite 6300</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Oakland, CA 94612</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<h3 id=\"by-phone\">By Phone</h3>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<table>\r\n<thead>\r\n<tr><th>Location</th><th>Methods of Payment</th></tr>\r\n</thead>\r\n<tbody>\r\n<tr><td style=\"background-color:white\">\r\nParking Citation Assistance Center\r\nPhone: (800) 500-6484\r\n</td><td>\r\nCredit cards: Visa, Mastercard, Discover or American Express\r\n</td></tr>\r\n</tbody>\r\n</table>', NULL, NULL, NULL, NULL, NULL, '2015-12-04 20:08:33', '2015-12-05 00:40:55', 'f20d6c8d-e817-4c50-a206-5cecfcf7d257');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_CTATitle`, `field_leadIn`, `field_CTALink`, `field_body`, `field_postDate`, `field_summary`, `field_about`, `field_departmentHead`, `field_departmentstaff`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('7', '11', 'en_us', 'Free Parking on Plaid Friday & Saturdays until New Year\'s', NULL, NULL, NULL, '<p>As part of the Shop Oakland Grown for the Holidays campaign, the City of Oakland will offer&nbsp;free parking every Saturday until New Year\'s Day plus Plaid Friday (November 27).&nbsp;The free parking applies to on-street meters (both individual meters and multi-space kiosks), four City-owned parking lots and four City-owned parking garages. Posted time limits&nbsp;at meters&nbsp;still apply.</p>', '2015-12-09 00:00:00', NULL, NULL, NULL, NULL, '2015-12-10 19:26:54', '2015-12-10 19:38:21', '6f49b0d7-8101-4859-a00a-2b4996348e91');
